void bubbleSort(int arr[], int n){
    for(int i = 0; i < n - 1; i++)
        for(int j = 0; j < n - 1; j++)
            if(arr[j] > arr[j + 1])
                swap(arr[j], arr[j + 1]);
}

int _Search (int arr[], int key){
    int l = 0, r = arr.size() + 1;
    while(r - l > 1){
        int mid = (l + r) / 2;
        if(arr[mid] > key) r = mid - 1;
        else if(arr[mid] < key) l = mid + 1;
        else break;
    }
}
